INSERT OR REPLACE INTO content (id, pillar_id, title, description, age_group, content_type, content_data) VALUES ('tech1_toddler', 1, 'The ""Ask, Don''t Tell"" Method', 'A technique to foster independence by asking questions instead of giving answers', 'toddler', 'technique', '{""instructions"":""Instead of telling your child what to do, ask questions that guide them to find solutions on their own."",""content"":""Use simple questions appropriate for their understanding.""}');
INSERT OR REPLACE INTO content (id, pillar_id, title, description, age_group, content_type, content_data) VALUES ('tech1_elementary', 1, 'The ""Ask, Don''t Tell"" Method', 'A technique to foster independence by asking questions instead of giving answers', 'elementary', 'technique', '{""instructions"":""Instead of telling your child what to do, ask questions that guide them to find solutions on their own."",""content"":""Ask more complex questions that encourage critical thinking.""}');
INSERT OR REPLACE INTO content (id, pillar_id, title, description, age_group, content_type, content_data) VALUES ('tech1_teen', 1, 'The ""Ask, Don''t Tell"" Method', 'A technique to foster independence by asking questions instead of giving answers', 'teen', 'technique', '{""instructions"":""Instead of telling your child what to do, ask questions that guide them to find solutions on their own."",""content"":""Guide them to resources where they can find answers independently.""}');
INSERT OR REPLACE INTO content (id, pillar_id, title, description, age_group, content_type, content_data) VALUES ('tech2_toddler', 2, 'The ""Yet"" Technique', 'A technique to foster a growth mindset by adding ""yet"" to statements', 'toddler', 'technique', '{""instructions"":""When your child says \""I can''t do this,\"" encourage them to add \""yet\"" to the end."",""content"":""Use simple language to introduce the concept of \""not yet\"".""}');
INSERT OR REPLACE INTO content (id, pillar_id, title, description, age_group, content_type, content_data) VALUES ('tech2_elementary', 2, 'The ""Yet"" Technique', 'A technique to foster a growth mindset by adding ""yet"" to statements', 'elementary', 'technique', '{""instructions"":""When your child says \""I can''t do this,\"" encourage them to add \""yet\"" to the end."",""content"":""Help them understand that abilities can grow with practice.""}');
INSERT OR REPLACE INTO content (id, pillar_id, title, description, age_group, content_type, content_data) VALUES ('tech2_teen', 2, 'The ""Yet"" Technique', 'A technique to foster a growth mindset by adding ""yet"" to statements', 'teen', 'technique', '{""instructions"":""When your child says \""I can''t do this,\"" encourage them to add \""yet\"" to the end."",""content"":""Connect the concept to their personal goals and aspirations.""}');
